Scripts.OnMouseDown:
if button == "LeftButton" and not self.isMoving then
 self:StartMoving();
 self.isMoving = true;
end

Scripts.OnMouseUp:
if button == "LeftButton" and self.isMoving then
 self:StopMovingOrSizing();
 self.isMoving = false;
end

Scripts.OnHide:
if ( self.isMoving ) then
 self:StopMovingOrSizing();
 self.isMoving = false;
end
